import { Context } from "../../context";
import Spinner from "../layout/Spinner";
import Track from "../tracks/Track";

const Tracks = () => {
  const [state] = useContext(Context);
  const { track_list, heading } = state;

  // if (track_list === undefined || track_list.length === 0) {
  //   return <Spinner />;
